import {React,  useState, useEffect } from 'react';
import axios from 'axios';
import Storage from './Storage';

const RecipeDetails = (props) => {

  const [recipe, setRecipe] = useState();
  const [ingredient, setngredient] = useState();
  const [ recipes, setRecipes] = useState ([]);



  useEffect(() => {
    getAlllrecipes();
 
  }, [ ]);

 
  function getAlllrecipes() {
    const AppId = "425e5776";
    const AppKey = "d2e0f595afefbf088189b8edc16a5ca9";
    axios.get(`https://api.edamam.com/search?q=${props.type}&app_id=${AppId}&app_key=${AppKey}`)
    .then((res) => {
      //  console.log(res.data);
      setRecipes(res.data.hits)
      setRecipe(res.data.hits[1].recipe)
         console.log("the recipe");
        console.log(res.data.hits[1].recipe);


    })
      .catch((err) => console.error(err));
    }


    return (
        <div>

<h1> {recipe.label} </h1>

          

  <div class="card " style={{width: "800px"}}>
  <div class="row g-0">
    <div class="col-md-4">
      <img
        src= {recipe.image}
        alt="..."
        class="img-fluid"
      />
    </div>
    <div class="col-md-8">
      <div class="card-body">
        <h5 class="card-title"> {recipe.label}</h5>
        <div class="card-text">

        <ul class="list-group">
          {setngredient(recipe.ingredientLines)}
        {ingredient.map((name, index) => (
        <li class="list-group-item" key={ index }>{name}      </li> 
        ))}
    </ul>

      
        </div>
        <p class="card-text">
          <small class="text-muted">Last updated 3 mins ago</small>
        </p>
      </div>
    </div>
  </div>
</div>
        </div>
    );
};

export default RecipeDetails;
